    private void BtnRegistarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnRegistarActionPerformed
        // TODO add your handling code here:
             
        for(int i=0;i<20;i++){
            if("obj".equals(jugador[i].getNombreJ())){
                jugador[i].setNombreJ(TxtNombreJ.getText());
                jugador[i].setPosicion(TxtPosicion.getText());
                jugador[i].setValorMer(Integer.parseInt(TxtValorM.getText()));
                jugador[i].setSueldo(Float.parseFloat(TxtSueldo.getText()));
                
                i=20;
                LblNotificacion.setText("Registro exitoso");
                
            }
        }
    }//GEN-LAST:event_BtnRegistarActionPerformed

