    private void BtnEliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnEliminarActionPerformed
        // TODO add your handling code here:
        if(TxtNombreJ.getText().equals("")==false){
            for(int i=0;i<20;i++){
                if(TxtNombreJ.getText().equals(jugador[i].getNombreJ())){
                   for(int j=i;j<20;j++){
                     if(jugador[j].getNombreJ().equals("obj")==false){
                   
                       jugador[j].setNombreJ(jugador[j+1].getNombreJ());
                       jugador[j].setPosicion(jugador[j+1].getPosicion());                            
                       jugador[j].setSueldo(jugador[j+1].getSueldo());                            
                       jugador[j].setValorMer(jugador[j+1].getValorMer());
                       LblNotificacion.setText("Dato eliminado");
                       i=20;
                        } 
                    }
                } 
                
            }
        }else if(TxtPosicion.getText().equals("")==false){
             for(int i=0;i<20;i++){
                if(TxtPosicion.getText().equals(jugador[i].getPosicion())){
                    for(int j=i;j<20;j++){
                        if(jugador[j].getPosicion().equals("obj")==false){
                           jugador[j].setNombreJ(jugador[j+1].getNombreJ());
                           jugador[j].setPosicion(jugador[j+1].getPosicion());                            
                           jugador[j].setSueldo(jugador[j+1].getSueldo());                            
                           jugador[j].setValorMer(jugador[j+1].getValorMer());
                           LblNotificacion.setText("Dato eliminado");
                            i=20;
                        }
                    }
                }
             }
               
        }else if(TxtSueldo.getText().equals("")==false){
            for(int i=0;i<20;i++){
                if(Float.parseFloat(TxtSueldo.getText())==jugador[i].getSueldo()){
                     for(int j=i;j<20;j++){
                        if(jugador[j].getSueldo()!=1){
                            jugador[j].setNombreJ(jugador[j+1].getNombreJ());
                            jugador[j].setPosicion(jugador[j+1].getPosicion());                            
                            jugador[j].setSueldo(jugador[j+1].getSueldo());                            
                            jugador[j].setValorMer(jugador[j+1].getValorMer());
                            LblNotificacion.setText("Dato eliminado");
                            i=20;
                        }
                    }
                }
            }
        }else if(TxtValorM.getText().equals("")==false){
           for(int i=0;i<20;i++){
                if(Integer.parseInt(TxtValorM.getText())==jugador[i].getValorMer()){
                     for(int j=i;j<20;j++){
                        if(jugador[j].getSueldo()!=1){
                            jugador[j].setNombreJ(jugador[j+1].getNombreJ());
                            jugador[j].setPosicion(jugador[j+1].getPosicion());                            
                            jugador[j].setSueldo(jugador[j+1].getSueldo());                            
                            jugador[j].setValorMer(jugador[j+1].getValorMer());
                            LblNotificacion.setText("Dato eliminado");
                            i=20;
                        }
                    }
                
            }
                
        }
           //Compara que la notificacion sea de dato eliminado y devuelve error al eliminar
           //get para obtener la cadena en el elemento en cuestion
           //Equals compara cadenas y devuelve true or false
        }if(LblNotificacion.getText().equals("Dato eliminado")==false){
                LblNotificacion.setText("Error al eliminar");
           }
        
        
    }//GEN-LAST:event_BtnEliminarActionPerformed
  
    
    private void BtnConsultarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnConsultarActionPerformed
        // TODO add your handling code here:
        if(TxtNombreJ.getText().equals("")==false){
            for(int i=0;i<20;i++){
                if(TxtNombreJ.getText().equals(jugador[i].getNombreJ())){
                     TxtNombreJ.setText(jugador[i].getNombreJ());
                     TxtPosicion.setText(jugador[i].getPosicion());                            
                     TxtSueldo.setText(String.valueOf(jugador[i].getSueldo()));                            
                     TxtValorM.setText(String.valueOf(jugador[i].getValorMer()));
                     LblNotificacion.setText("Dato Consultado");
                       i=20;
                        } 
                    }
                
        }else if(TxtPosicion.getText().equals("")==false){
             for(int i=0;i<20;i++){
                if(TxtPosicion.getText().equals(jugador[i].getPosicion())){
                    TxtNombreJ.setText(jugador[i].getNombreJ());
                     TxtPosicion.setText(jugador[i].getPosicion());                            
                     TxtSueldo.setText(String.valueOf(jugador[i].getSueldo()));                            
                     TxtValorM.setText(String.valueOf(jugador[i].getValorMer()));
                     LblNotificacion.setText("Dato Consultado");
                       i=20;
                        }
                    }
             
        }else if(TxtSueldo.getText().equals("")==false){
            for(int i=0;i<20;i++){
                if(Integer.parseInt(TxtSueldo.getText())==jugador[i].getSueldo()){
                     TxtNombreJ.setText(jugador[i].getNombreJ());
                     TxtPosicion.setText(jugador[i].getPosicion());                            
                     TxtSueldo.setText(String.valueOf(jugador[i].getSueldo()));                            
                     TxtValorM.setText(String.valueOf(jugador[i].getValorMer()));
                     LblNotificacion.setText("Dato Consultado");
                       i=20;
                        }
                    }
            
        }else if(TxtValorM.getText().equals("")==false){
           for(int i=0;i<20;i++){
                if(Integer.parseInt(TxtValorM.getText())==jugador[i].getValorMer()){
                     TxtNombreJ.setText(jugador[i].getNombreJ());
                     TxtPosicion.setText(jugador[i].getPosicion());                            
                     TxtSueldo.setText(String.valueOf(jugador[i].getSueldo()));                            
                     TxtValorM.setText(String.valueOf(jugador[i].getValorMer()));
                     LblNotificacion.setText("Dato Consultado");
                       i=20;
                        
                    }
                }
            
           //Compara que la notificacion sea de dato eliminado y devuelve error al eliminar
           //get para obtener la cadena en el elemento en cuestion
           //Equals compara cadenas y devuelve true or false
        }else {
                LblNotificacion.setText("Error al consultar");
           }
    }//GEN-LAST:event_BtnConsultarActionPerformed

